import React, {Component} from 'react';
import './App.css';
const HIDDEN_SYMBOL = "_";
const Lettre = ({ lettre, feedback }) => (
  <div className={`lettre ${feedback}`}>
    <span className="lettre">
      {feedback === 'hidden' ? HIDDEN_SYMBOL : lettre}
    </span>
  </div>
)
const Entree = ({ lettre, feedback, onClick }) => (
  <div className={`entree ${feedback}`} onClick={() => onClick(lettre)}>
    <span className="symbol">
      {lettre}
    </span>
  </div>
)
function getAllIndexes (arr, val){
      let indexes = [], i;
      for(i = 0; i < arr.length; i++)
          if (arr[i] === val)
              indexes.push(i);
      return indexes;
  }
let count = 0  
class App extends Component {

  handleClick = (entree) => {

    if(this.state.mot.indexOf(entree) !== -1){
      let indexes = getAllIndexes(this.state.mot, entree);
      //console.log(indexes);
      let lettre = [ ...this.state.lettre ]
      let matchedIndices = [ ...this.state.matchedIndices ]

      indexes.map(function(index) {
        matchedIndices[index] = {...matchedIndices[index], ...lettre[index].name}
        lettre[index] = {...lettre[index], feedback: "visible"}
        return 1
      })


      this.setState({ lettre })
      this.setState({ matchedIndices })

      matchedIndices.map(function(index){
        console.log(index[0])
        count ++
        console.log(count)
        return 1
      })
      console.log(this.state.mot)
      console.log(matchedIndices)
      //if(arrayEquals(this.state.mot,matchedIndices))
      //alert("Vous avez gagnez")
      //this.setState({lettre.feedback: "visible"})
      /*
        this.setState(
          (prevState, props) => ({ })
        )
      */
    }

  }
  state = {
    matchedIndices : [],
    mot :["S", "E", "R", "P", "E", "N", "T"],

   lettre :[
    {id:1, name:"S", feedback: "hidden"},
    {id:2, name:"E", feedback: "hidden"},
    {id:3, name:"R", feedback: "hidden"},
    {id:4, name:"P", feedback: "hidden"},
    {id:5, name:"E", feedback: "hidden"},
    {id:6, name:"N", feedback: "hidden"},
    {id:7, name:"T", feedback: "hidden"}
  ],
   entree : [
    {id:1, name:"A"},
    {id:2, name:"B"},
    {id:3, name:"C"},
    {id:4, name:"D"},
    {id:5, name:"E"},
    {id:6, name:"F"},
    {id:7, name:"G"},
    {id:8, name:"H"},
    {id:9, name:"I"},
    {id:10, name:"J"},
    {id:11, name:"K"},
    {id:12, name:"L"},
    {id:13, name:"M"},
    {id:14, name:"N"},
    {id:15, name:"O"},
    {id:16, name:"P"},
    {id:17, name:"Q"},
    {id:18, name:"R"},
    {id:19, name:"S"},
    {id:20, name:"T"},
    {id:21, name:"U"},
    {id:22, name:"V"},
    {id:23, name:"W"},
    {id:24, name:"X"},
    {id:25, name:"Y"},
    {id:26, name:"Z"},
   ]
   }
 render () {

   const { lettre, entree} = this.state

    return (
      <div className="App">
      {lettre.map((lettre, index) => (
            <Lettre lettre={lettre.name} feedback={lettre.feedback} key={index} />
       ))}
      {entree.map((entree, index) => (

            <Entree lettre={entree.name} feedback="visible" onClick={this.handleClick} key={index}   />
       ))}

      </div>
    )
  }

}

