import React, {Component} from 'react';
import './App.css';
const HIDDEN_SYMBOL = "_";
const Lettre = ({ lettre, feedback }) => (
  <div className={`lettre ${feedback}`}>
    <span className="lettre">
      {feedback === 'hidden' ? HIDDEN_SYMBOL : lettre}
    </span>
  </div>
)
const Entree = ({ lettre, feedback, range, onClick }) => (
  <div className={`entree ${feedback} ${range}`} onClick={() => onClick(lettre)}>
    <span className="symbol">
      {lettre}
    </span>
  </div>
)
function getAllIndexes (arr, val){
      let indexes = [], i;
      for(i = 0; i < arr.length; i++)
          if (arr[i] === val)
              indexes.push(i);
      return indexes;
  }
let count = 0 
let copy = [] 
class App extends Component {

  handleClick = (boutonClique) => {
    let entree = [ ...this.state.entree ]
      let compteur = 0
      entree.map(function(index) {
        if(index.name ===  boutonClique){
          entree[compteur] = {...entree[compteur], feedback: "unclickable"}
        }
        compteur++
          return 1
      })
    if(this.state.mot.indexOf(boutonClique) !== -1 && this.state.won === false){
      let indexes = getAllIndexes(this.state.mot, boutonClique);
      let lettre = [ ...this.state.lettre ]
      let matchedIndices = [ ...this.state.matchedIndices ]

      indexes.map(function(index) {
        matchedIndices[index] = {...matchedIndices[index], ...lettre[index].name}
        lettre[index] = {...lettre[index], feedback: "visible"}

        return 1
      })


      this.setState({ lettre })
      this.setState({ matchedIndices })
      this.setState({ entree })
      copy = this.state.mot
      count = 0
      matchedIndices.map(function(value, key){
        if(value !== undefined && copy[key] === value[0])
        {
          count = count + 1
        }
        return 1
      })
      if(count === this.state.mot.length){
        this.setState({ won: true })
        alert("Vous avez gagné")
      }


    }

  }
  state = {
    won : false,
    matchedIndices : [],
    mot :["S", "E", "R", "P", "E", "N", "T"],

   lettre :[
    {id:1, name:"S", feedback: "hidden"},
    {id:2, name:"E", feedback: "hidden"},
    {id:3, name:"R", feedback: "hidden"},
    {id:4, name:"P", feedback: "hidden"},
    {id:5, name:"E", feedback: "hidden"},
    {id:6, name:"N", feedback: "hidden"},
    {id:7, name:"T", feedback: "hidden"}
  ],
   entree : [
    {id:1, name:"A" , feedback: "clickable"},
    {id:2, name:"B", feedback: "clickable"},
    {id:3, name:"C", feedback: "clickable"},
    {id:4, name:"D", feedback: "clickable"},
    {id:5, name:"E", feedback: "clickable"},
    {id:6, name:"F", feedback: "clickable"},
    {id:7, name:"G", feedback: "clickable"},
    {id:8, name:"H", feedback: "clickable"},
    {id:9, name:"I", feedback: "clickable"},
    {id:10, name:"J", feedback: "clickable"},
    {id:11, name:"K", feedback: "clickable"},
    {id:12, name:"L", feedback: "clickable"},
    {id:13, name:"M", feedback: "clickable"},
    {id:14, name:"N", feedback: "clickable"},
    {id:15, name:"O", feedback: "clickable"},
    {id:16, name:"P", feedback: "clickable"},
    {id:17, name:"Q", feedback: "clickable"},
    {id:18, name:"R", feedback: "clickable"},
    {id:19, name:"S", feedback: "clickable"},
    {id:20, name:"T", feedback: "clickable"},
    {id:21, name:"U", feedback: "clickable"},
    {id:22, name:"V", feedback: "clickable"},
    {id:23, name:"W", feedback: "clickable"},
    {id:24, name:"X", feedback: "clickable"},
    {id:25, name:"Y", feedback: "clickable"},
    {id:26, name:"Z", feedback: "clickable"},
   ]
   }
 render () {

   const { lettre, entree} = this.state

    return (
      <div className="App">
      {lettre.map((lettre, index) => (
            <Lettre lettre={lettre.name} feedback={lettre.feedback} key={index} />
       ))}
      {entree.map((entree, index) => (

            <Entree lettre={entree.name} feedback={entree.feedback} range={index < 13 ? 'firstRange' : 'secondRange'} onClick={this.handleClick} key={index}   />

      ))}

      </div>
    )
  }

}

