import React, {Component} from 'react';
import './App.css';
const HIDDEN_SYMBOL = "_";
const Lettre = ({ lettre, feedback }) => (
  <div className={`lettre ${feedback}`}>
    <span className="lettre">
      {feedback === 'hidden' ? HIDDEN_SYMBOL : lettre}
    </span>
  </div>
)
class App extends Component {
 render () {
   const lettre = [
    {id:1, name:"S"},
    {id:2, name:"E"},
    {id:3, name:"R"},
    {id:4, name:"P"},
    {id:5, name:"E"},
    {id:6, name:"N"},
    {id:7, name:"T"}
   ]
    return (
      <div className="App">
      {lettre.map((lettre, index) => (
            <Lettre lettre={lettre.name} feedback="visible" index={index} />
            ))}
      </div>
    )
  }

}

