import React from 'react';
import './App.css';
const HIDDEN_SYMBOL = "_";
const mot = [
  {id: 1, name: 'SERPENT'},
  {id: 2, name: 'AGNEAU'},
]
const Lettre = ({ lettre, feedback }) => (
  <div className={`lettre ${feedback}`}>
    <span className="lettre">
      {feedback === 'hidden' ? HIDDEN_SYMBOL : lettre}
    </span>
  </div>
)
function App() {
  return (
    <div className="App">
    {this.mot.map((mot, index) => (
      <Lettre lettre={mot.name} feedback="hidden" index={index} />

    ))}
    </div>
  );
}

export default App;
