import React, {Component} from 'react';
import './App.css';
const HIDDEN_SYMBOL = "_";
const Lettre = ({ lettre, feedback }) => (
  <div className={`lettre ${feedback}`}>
    <span className="lettre">
      {feedback === 'hidden' ? HIDDEN_SYMBOL : lettre}
    </span>
  </div>
)
const Entree = ({ lettre, feedback }) => (
  <div className={`entree ${feedback}`}>
    <span >
      {lettre}
    </span>
  </div>
)

class App extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick() {
    console.log('Cliqué');
  }
  render() {
    return <button onClick={this.handleClick}>Cliquez ici</button>;
  }
}


