import React, {Component} from 'react';
import './App.css';
const HIDDEN_SYMBOL = "_";
const Lettre = ({ lettre, feedback }) => (
  <div className={`lettre ${feedback}`}>
    <span className="lettre">
      {feedback === 'hidden' ? HIDDEN_SYMBOL : lettre}
    </span>
  </div>
)
const Entree = ({ lettre, feedback }) => (
  <div className={`entree ${feedback}`}>
    <span >
      {lettre}
    </span>
  </div>
)

class App extends Component {
  handleClick = (entree) => {
  console.log(entree)
}
 render () {
   const lettre = [
    {id:1, name:"S"},
    {id:2, name:"E"},
    {id:3, name:"R"},
    {id:4, name:"P"},
    {id:5, name:"E"},
    {id:6, name:"N"},
    {id:7, name:"T"}
   ]
   const entree = [
    {id:1, name:"A"},
    {id:2, name:"B"},
    {id:3, name:"C"},
    {id:4, name:"D"},
    {id:5, name:"E"},
    {id:6, name:"F"},
    {id:7, name:"G"},
    {id:8, name:"H"},
    {id:9, name:"I"},
    {id:10, name:"J"},
    {id:11, name:"K"},
    {id:12, name:"L"},
    {id:13, name:"M"},
    {id:14, name:"N"},
    {id:15, name:"O"},
    {id:16, name:"P"},
    {id:17, name:"Q"},
    {id:18, name:"R"},
    {id:19, name:"S"},
    {id:20, name:"T"},
    {id:21, name:"U"},
    {id:22, name:"V"},
    {id:23, name:"W"},
    {id:24, name:"X"},
    {id:25, name:"Y"},
    {id:26, name:"Z"},
   ]
    return (
      <div className="App">
      {lettre.map((lettre, index) => (
            <Lettre lettre={lettre.name} feedback="hidden" key={index} />
       ))}

      {entree.map((entree, index) => (
            <Entree lettre={entree.name} feedback="hidden" key={index}  onClick={this.handleClick(entree)} />
       ))}

      </div>
    )
  }

}

