import React from 'react';
import './App.css';
const HIDDEN_SYMBOL = "_";
const lettre = [
  {name:'S'},
  {name:'U'},
]
class LoginScreen extends Component {
  constructor(props) {
    super(props)
    this.logIn = (event) => {
      // …
    }
  }

}
const Lettre = ({ lettre, feedback }) => (
  <div className={`lettre ${feedback}`}>
    <span className="lettre">
      {feedback === 'hidden' ? HIDDEN_SYMBOL : lettre}
    </span>
  </div>
)
function App() {
  return (
    <div className="App">
    {this.lettre.map((lettre, index) => (
      <Lettre lettre={lettre.name} feedback="hidden" index={index} />

    ))}
    </div>
  );
}

export default App;
